import React from "react";
import AnimalCard from "../../reusable/AnimalCard";
import Grid from "@mui/material/Grid";
import Box from "@mui/system/Box";
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { centeredContainer } from "../../styles";
import { Container } from "@mui/system";

const koalaImg = require("/Users/Vlad/second-app/src/assets/koala.jpg");
const lionImg = require("/Users/Vlad/second-app/src/assets/lion.jpg");
const lemurImg = require("/Users/Vlad/second-app/src/assets/lemur.jpg");
const asianLemurImg = require("/Users/Vlad/second-app/src/assets/asian_lemur.jpg");
const data = [
    [koalaImg, "Koala", "The koala or, inaccurately, koala bear (Phascolarctos cinereus) is an arboreal herbivorous marsupial native to Australia. It is the only extant representative of the family Phascolarctidae.", 120],
    [lionImg, "Lion", "The lion (Panthera leo) is a large cat of the genus Panthera native to Africa and India. It has a muscular, broad-chested body, short, rounded head, round ears, and a hairy tuft at the end of its tail. ", 324],
    [lemurImg, "Lemur", "They are endemic to the island of Madagascar. Most existing lemurs are small, have a pointed snout, large eyes, and a long tail. They chiefly live in trees and are active at night.", 234],
    [asianLemurImg, "Asian Lemur", "Their closest evolutionary relatives are primates. There are just two living species of colugos: the Sunda flying lemur (Galeopterus variegatus) and the Philippine flying lemurs.", 100]

];

const Cards = () => {
  return (
      <Box sx={centeredContainer}>
        <Container sx={{display: "flex", justifyContent: "center", marginBottom:3}}>
        <Typography variant="h4" mb="10px">Our animals</Typography>
        </Container>

        <Grid container spacing={2} sx={{justifyContent: "space-between"}}>
          {data.map((record, id) => 
            <Grid item key={id}>
            <AnimalCard 
              imgSrc={record[0]}
              imgAlt={record[1]}
              cardTitle={record[1]}
              cardText={record[2]}
              cardPrice={record[3]}
            />
          </Grid>
          )}
        </Grid>
        <Grid container sx={{my: 5, justifyContent: "center"}}>
        <Button variant="contained" size="large" sx={{bgcolor:"#01A66F"}} >More animals</Button>
        </Grid>
      </Box>
  );
};

export default Cards;
