import React from "react";
import Grid from "@mui/material/Grid";
import { Box, Typography } from "@mui/material";
import {useStyles, centeredContainer} from './styles.js';

const filmPicture = require("../assets/photo-film.jpg");

const HeroBlock = () => {
  const classes = useStyles()

  return (
    <Box sx={centeredContainer} >
      <Grid container spacing={2}>
        <Grid item xs>
          <Typography variant="h2">Some text about films</Typography>
          <Typography variant="body1" sx={{mt:2}}>
          A zoo is a 
            facility in which animals are housed within enclosures, cared for, displayed to the public, 
            and in some cases bred for conservation purposes.
          </Typography>
        </Grid>
        <Grid item xs>
          <Box
            component="img"
            src={filmPicture}
            className={classes.bigImage}
          />
        </Grid>
      </Grid>
    </Box>
  );
};

export default HeroBlock;
