import React from "react";
import ZooCard from "./reusable/FilmCard";
import Grid from "@mui/material/Grid";
import Box from "@mui/system/Box";
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { centeredContainer } from "./styles";

const koalaImg = require("/Users/Vlad/second-app/src/assets/koala.jpg");
const lionImg = require("/Users/Vlad/second-app/src/assets/lion.jpg");
const lemurImg = require("/Users/Vlad/second-app/src/assets/lemur.jpg");
const asianLemurImg = require("/Users/Vlad/second-app/src/assets/asian_lemur.jpg");
const data = [
    [koalaImg, "Fight Club", "An insomniac office worker and a devil-may-care soap maker form an underground fight club that evolves into much more.", 120],
    [lionImg, "Interstellar", "A team of explorers travel through a wormhole in space in an attempt to ensure humanity's survival.", 324],
    [lemurImg, "The Wolf of Wall Street", "Based on the true story of Jordan Belfort, from his rise to a wealthy stock-broker living the high life to his fall involving crime, corruption and the federal government.", 234],
    [asianLemurImg, "Joker", "A mentally troubled stand-up comedian embarks on a downward spiral that leads to the creation of an iconic villain.", 100]

];

const Cards = () => {
  return (
      <Box sx={centeredContainer}>

        <Typography variant="h4" mb="10px" sx={{alignItems: "center"}}>Our animals</Typography>
        <Grid container spacing={2} sx={{justifyContent: "space-between"}}>
          {data.map((record, id) => 
            <Grid item key={id}>
            <ZooCard
              imgSrc={record[0]}
              imgAlt={record[1]}
              cardTitle={record[1]}
              cardText={record[2]}
              cardPrice={record[3]}
            />
          </Grid>
          )}
        </Grid>
        <Grid container sx={{my: 5, justifyContent: "center"}}>
        <Button variant="contained" size="large" >More films</Button>
        </Grid>
      </Box>
  );
};

export default Cards;
